import React, { useEffect, useState } from "react";
import {
  List,
  Datagrid,
  TextField,
  EditButton,
  DeleteButton,
  DateField,
  SingleFieldList,
  ChipField,
  ReferenceArrayField,
  ArrayField,
  FilterButton,
  TextInput,
  FilterForm,
  CreateButton,
  FilterList,
  FilterListItem,
} from "react-admin";
import { Calendar } from "react-date-range";
import { Box, Card, CardContent, styled } from "@mui/material";
import axios from "axios";
import { URI } from "../URLS";
import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css";
const RegionFilter = () => {
  const selectionRange = {
    startDate: new Date(),
    endDate: new Date(),
    key: "selection",
  };
  const handleSelect = (date) => {
    console.log(date); // native Date object
  };
  const [region, setRegion] = useState([]);
  useEffect(() => {
    axios
      .get(`${URI}/getRegions?employeeId=admin@pixomnia.com`)
      .then((data) => {
        setRegion(data.data);
      });
  }, [setRegion]);
  console.log(region);
  return (
    <FilterList label="Regions" defaultChecked="TX">
      {region.map((item, index) => (
        <FilterListItem
          label={item.regionId}
          value={{ region: item.regionId }}
          key={index}
          defaultChecked
        />
      ))}
      {/* <FilterListItem>test</FilterListItem> */}
    </FilterList>
  );
};

const TimeFilter = () => {
  return (
    <FilterList label="Days">
      <FilterListItem label="1 Day" value={{ day: 1 }} />
      <FilterListItem label="2 Days" value={{ day: 2 }} />
      <FilterListItem label="1 Week" value={{ day: 7 }} />
      <FilterListItem label="2 Weeks" value={{ day: 14 }} />
      <FilterListItem label="1 Month" value={{ day: 30 }} />
    </FilterList>
  );
};

const FilterCloseShift = (props) => {
  return (
    <Box
      sx={{
        display: {
          xs: "none",
          sm: "block",
        },
        order: -1, // display on the left rather than on the right of the list
        width: "13.5em",
        marginRight: "0.5em",
        marginTop: "4em",
      }}
    >
      <Card>
        <CardContent>
          <RegionFilter />
          <TimeFilter />
        </CardContent>
      </Card>
    </Box>
  );
};

export default FilterCloseShift;
