import React, { useEffect, useState } from "react";
import {
  List,
  Datagrid,
  TextField,
  EditButton,
  DeleteButton,
  DateField,
  SingleFieldList,
  ChipField,
  ReferenceArrayField,
  ArrayField,
  FilterButton,
  TextInput,
  FilterForm,
  CreateButton,
  FilterList,
  FilterListItem,
  SelectInput,
} from "react-admin";
import { Box, Card, CardContent, styled } from "@mui/material";
import axios from "axios";
import { URI } from "../URLS";
const RegionFilter = () => {
  const [regions, setRegions] = useState([]);
  console.log("regions", regions);
  useEffect(() => {
    axios
      .get(`${URI}/getRegions?employeeId=admin@pixomnia.com`)
      .then((data) => {
        const reg = data.data.map((item) => {
          return { id: item.regionId, name: item.regionId };
        });
        setRegions(reg);
      });
  }, [setRegions]);
  return (
    <FilterList label="Regions" defaultValue={{ region: "TX" }}>
      <SelectInput source="regionId" choices={regions} />
    </FilterList>
  );
};

const FilterShifts = (props) => {
  return (
    <Box
      sx={{
        display: {
          xs: "none",
          sm: "block",
        },
        order: -1, // display on the left rather than on the right of the list
        width: "15em",
        marginRight: "1em",
        marginTop: "4em",
      }}
    >
      <Card>
        <CardContent>
          <RegionFilter />
        </CardContent>
      </Card>
    </Box>
  );
};

export default FilterShifts;
