import React, { useEffect, useState } from "react";
import {
  List,
  Datagrid,
  TextField,
  EditButton,
  DeleteButton,
  DateField,
  SingleFieldList,
  ChipField,
  ReferenceArrayField,
  ArrayField,
  FilterButton,
  TextInput,
  FilterForm,
  CreateButton,
  FilterList,
  FilterListItem,
  SelectInput,
  SimpleForm,
} from "react-admin";
import { Box, Card, CardContent, styled } from "@mui/material";
import axios from "axios";
import { URI } from "../URLS";

const FilterShifts = (props) => {
  const [regions, setRegions] = useState({ id: "TX", name: "TX" });
  const [places, setPlaces] = useState(false);
  console.log("regions", regions);
  useEffect(() => {
    axios
      .get(`${URI}/getRegions?employeeId=admin@pixomnia.com`)
      .then((data) => {
        console.log(data);
        const reg = data.data.map((item) => {
          return { id: item.regionId, name: item.regionId };
        });
        setRegions(reg);
      });
  }, [setRegions]);

  const RegionFilter = () => {
    return (
      <FilterList label="Regions" defaultValue={{ region: "TX" }}>
        {places.map((item) => (
          <FilterListItem
            label={item.placeId}
            value={{ region: item.placeId }}
          />
        ))}
      </FilterList>
    );
  };
  const getPlaces = (item) => {
    axios
      .get(
        `${URI}/getPlacesWithInfo?employeeId=admin@pixomnia&regionId=${item}`
      )
      .then((data) => setPlaces(data.data));
  };
  return (
    <Box
      sx={{
        display: {
          xs: "none",
          sm: "block",
        },
        order: -1, // display on the left rather than on the right of the list
        width: "15em",
        marginRight: "1em",
        marginTop: "4em",
      }}
    >
      <Card>
        <CardContent>
          <SimpleForm>
            <SelectInput
              source="regionId"
              choices={regions}
              onChange={(i) => getPlaces(i.target.value)}
            />
            {places && <RegionFilter />}
          </SimpleForm>
          {/* <RegionFilter /> */}
        </CardContent>
      </Card>
    </Box>
  );
};

export default FilterShifts;
