import { getPermissionsFromRoles } from "@react-admin/ra-rbac";
const roles = {
  accountant: [{ type: "deny", action: "read", resource: "getRegions" }],
};

const authProvider = {
  checkAuth: () => {
    const role = localStorage.getItem("role");
    return role ? Promise.resolve() : Promise.reject();
  },
  login: ({ role }) => {
    localStorage.setItem("role", role);
    return Promise.resolve();
  },
  logout: () => {
    localStorage.removeItem("role");
    return Promise.resolve();
  },
  checkError: () => Promise.resolve(),
  getIdentity: () => {
    const role = localStorage.getItem("role");
    return Promise.resolve({
      id: role,
      fullName: {
        accountant: "Accountant",
        contentEditor: "Content Editor",
        stockManager: "Stock manager",
        administrator: "Administrator",
      }[role],
    });
  },
  getPermissions: () => {
    return Promise.resolve([
      { type: "deny", action: "read", resource: "getPrinters" },
    ]);
  },
  //   getRoles: () => Promise.resolve(roles),
};

export default authProvider;
