import axios from "axios";
import React, { useEffect, useState } from "react";
import {
  Create,
  SimpleForm,
  TextInput,
  SelectArrayInput,
  SelectInput,
} from "react-admin";
import { URI } from "../../URLS";

const StorageCreate = (props) => {
  const [regions, setRegions] = useState([]);
  console.log("regions", regions);
  useEffect(() => {
    axios
      .get(`${URI}/getRegions?employeeId=admin@pixomnia.com`)
      .then((data) => {
        const reg = data.data.map((item) => {
          return { id: item.regionId, name: item.regionId };
        });
        setRegions(reg);
      });
  }, [setRegions]);
  const action = [{ id: "Transfer", name: "Transfer" }];
  return (
    <Create
      title="Create a printer"
      {...props}
      resource="addStorage"
      redirect="/getStorages"
    >
      <SimpleForm>
        <SelectInput source="action" choices={action} />
        <TextInput source="email" title="Email" />
        <TextInput source="role" title="Role" />
        <TextInput source="note" title="Note" />
      </SimpleForm>
    </Create>
  );
};

export default StorageCreate;
